export interface WorkExperienceType {
  id: number;
  company: string;
  slug: string;
  position: string;
  location: string;
  startYear: string;
  endYear: string;
  shortDescription: string;
  fullDescription: string;
  technologies: string[];
  tags: string[];
  highlights: string[];
}

export const workExperience: WorkExperienceType[] = [
  {
    id: 1,
    company: "TechCorp Solutions",
    slug: "techcorp",
    position: "Senior Frontend Developer",
    location: "San Francisco, CA",
    startYear: "2022",
    endYear: "Present",
    shortDescription: "Lead frontend development for enterprise SaaS platform serving 100k+ users",
    fullDescription: "Leading a team of 4 frontend developers in building and maintaining a complex enterprise SaaS platform. Responsible for architecture decisions, code reviews, and mentoring junior developers. Implemented micro-frontend architecture that improved deployment speed by 40% and reduced bundle size by 30%. Collaborated closely with UX/UI designers to create intuitive user interfaces and worked with backend teams to optimize API integrations.",
    technologies: ["React", "TypeScript", "Redux Toolkit", "Material-UI", "Webpack", "Jest", "Cypress"],
    tags: ["Leadership", "Architecture", "Performance"],
    highlights: [
      "Reduced page load time by 50%",
      "Implemented automated testing pipeline",
      "Mentored 3 junior developers"
    ]
  },
  {
    id: 2,
    company: "StartupXYZ",
    slug: "startupxyz",
    position: "Frontend Developer",
    location: "Austin, TX",
    startYear: "2021",
    endYear: "2022",
    shortDescription: "Built responsive web applications for fintech startup from ground up",
    fullDescription: "Joined as the second frontend developer at a fast-growing fintech startup. Built the entire customer-facing web application from scratch using React and modern development practices. Worked directly with the design team to implement pixel-perfect UIs and collaborated with backend developers to integrate complex financial APIs. Implemented real-time features using WebSocket connections and built a comprehensive dashboard for financial data visualization.",
    technologies: ["React", "JavaScript", "Styled Components", "Chart.js", "WebSocket", "REST APIs"],
    tags: ["Startup", "Fintech", "Real-time"],
    highlights: [
      "Built complete web app from scratch",
      "Implemented real-time data visualization",
      "Achieved 99.9% uptime"
    ]
  },
  {
    id: 3,
    company: "Digital Agency Pro",
    slug: "digitalagency",
    position: "Junior Frontend Developer",
    location: "Remote",
    startYear: "2020",
    endYear: "2021",
    shortDescription: "Developed client websites and e-commerce platforms using modern web technologies",
    fullDescription: "Started my professional career at a digital agency, working on various client projects ranging from corporate websites to complex e-commerce platforms. Gained experience with different CMS systems and e-commerce solutions. Collaborated with designers and project managers to deliver projects on time and within budget. Focused on creating responsive, accessible, and SEO-friendly websites while learning best practices in web development.",
    technologies: ["HTML5", "CSS3", "JavaScript", "WordPress", "Shopify", "SASS", "jQuery"],
    tags: ["Agency", "E-commerce", "WordPress"],
    highlights: [
      "Delivered 15+ client projects",
      "Improved site speed by 35% on average",
      "Learned multiple CMS platforms"
    ]
  }
];
