import React, { useState } from 'react';
import { workExperience } from '../data/workExperience';

const WorkExperience = ({ experience, index }) => {
  const [showFullDescription, setShowFullDescription] = useState(false);

  return (
    <div 
      style={{ animationDelay: `${index * 0.2}s` }}
      className="group bg-white/80 backdrop-blur-sm rounded-3xl shadow-lg border border-neutral-200/50 p-8 hover:shadow-2xl transition-all duration-500 hover:-translate-y-2 animate-fade-in-up"
    >
      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-start md:justify-between mb-6">
        <div className="flex-1">
          <h3 className="text-2xl font-black text-neutral-900 mb-2 group-hover:text-neutral-700 transition-colors">
            {experience.position}
          </h3>
          <p className="text-xl text-neutral-600 font-semibold mb-1">{experience.company}</p>
          <p className="text-sm text-neutral-500">{experience.location}</p>
        </div>
        <div className="mt-3 md:mt-0">
          <span className="inline-flex items-center px-4 py-2 rounded-full text-sm font-semibold bg-neutral-900 text-white shadow-md">
            {experience.startYear} - {experience.endYear}
          </span>
        </div>
      </div>

      {/* Tags */}
      <div className="flex flex-wrap gap-3 mb-6">
        {experience.tags.map((tag, tagIndex) => (
          <span
            key={tag}
            style={{ animationDelay: `${tagIndex * 0.1}s` }}
            className="inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold bg-neutral-100 text-neutral-700 hover:bg-neutral-900 hover:text-white transition-all duration-300 animate-fade-in-delay"
          >
            {tag}
          </span>
        ))}
      </div>

      {/* Description */}
      <div className="mb-6">
        <p className="text-neutral-700 leading-relaxed text-lg">
          {showFullDescription ? experience.fullDescription : experience.shortDescription}
        </p>
        <button
          onClick={() => setShowFullDescription(!showFullDescription)}
          className="group mt-4 inline-flex items-center text-neutral-600 hover:text-neutral-900 font-semibold text-sm transition-all duration-300"
        >
          {showFullDescription ? 'Show Less' : 'Show Full Description'}
          <svg 
            className={`ml-2 w-4 h-4 transition-transform duration-300 ${showFullDescription ? 'rotate-180' : ''} group-hover:translate-x-1`}
            fill="none" 
            stroke="currentColor" 
            viewBox="0 0 24 24"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
          </svg>
        </button>
      </div>

      {/* Highlights */}
      {showFullDescription && experience.highlights && (
        <div className="mb-4">
          <h4 className="text-sm font-semibold text-gray-900 mb-2">Key Achievements:</h4>
          <ul className="list-disc list-inside space-y-1">
            {experience.highlights.map((highlight, index) => (
              <li key={index} className="text-sm text-gray-700">{highlight}</li>
            ))}
          </ul>
        </div>
      )}

      {/* Technologies */}
      <div>
        <h4 className="text-sm font-bold text-neutral-900 mb-4">Technologies & Tools:</h4>
        <div className="flex flex-wrap gap-2">
          {experience.technologies.map((tech, techIndex) => (
            <span
              key={tech}
              style={{ animationDelay: `${techIndex * 0.05}s` }}
              className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-neutral-50 text-neutral-700 hover:bg-neutral-900 hover:text-white transition-all duration-300 hover:scale-105 animate-fade-in-delay border border-neutral-200"
            >
              {tech}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
};

const Work = () => {
  return (
    <div className="space-y-16">
      {/* Header */}
      <div className="text-center animate-fade-in-up">
        <h1 className="text-6xl md:text-7xl font-black text-neutral-900 mb-6 leading-tight">
          Work Experience
        </h1>
        <p className="text-xl text-neutral-600 max-w-3xl mx-auto leading-relaxed">
          My professional journey in frontend development, from junior developer to senior roles, 
          building impactful web applications across different industries.
        </p>
      </div>

      {/* Experience Timeline */}
      <div className="space-y-12">
        {workExperience.map((experience, index) => (
          <WorkExperience key={experience.id} experience={experience} index={index} />
        ))}
      </div>

      {/* Skills Summary */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Technical Expertise</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          <div>
            <h3 className="font-semibold text-gray-900 mb-3">Frontend Frameworks</h3>
            <div className="space-y-2">
              {['React.js', 'Vue.js', 'Angular', 'Next.js'].map((skill) => (
                <div key={skill} className="flex items-center">
                  <div className="w-2 h-2 bg-primary-500 rounded-full mr-3"></div>
                  <span className="text-gray-700">{skill}</span>
                </div>
              ))}
            </div>
          </div>
          <div>
            <h3 className="font-semibold text-gray-900 mb-3">Languages</h3>
            <div className="space-y-2">
              {['JavaScript (ES6+)', 'TypeScript', 'HTML5', 'CSS3/SASS'].map((skill) => (
                <div key={skill} className="flex items-center">
                  <div className="w-2 h-2 bg-primary-500 rounded-full mr-3"></div>
                  <span className="text-gray-700">{skill}</span>
                </div>
              ))}
            </div>
          </div>
          <div>
            <h3 className="font-semibold text-gray-900 mb-3">Tools & Platforms</h3>
            <div className="space-y-2">
              {['Git/GitHub', 'Webpack', 'Jest/Cypress', 'Docker'].map((skill) => (
                <div key={skill} className="flex items-center">
                  <div className="w-2 h-2 bg-primary-500 rounded-full mr-3"></div>
                  <span className="text-gray-700">{skill}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Call to Action */}
      <div className="text-center bg-primary-50 rounded-lg p-8">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Interested in Working Together?</h2>
        <p className="text-gray-700 mb-6 max-w-xl mx-auto">
          I'm always open to discussing new opportunities and interesting projects. 
          Let's connect and see how we can create something amazing together.
        </p>
        <div className="flex flex-col sm:flex-row gap-4 justify-center">
          <a
            href="mailto:tam.le@example.com"
            className="bg-primary-600 text-white px-6 py-3 rounded-lg font-medium hover:bg-primary-700 transition-colors"
          >
            Get In Touch
          </a>
          <a
            href="/resume.pdf"
            target="_blank"
            rel="noopener noreferrer"
            className="border border-primary-600 text-primary-600 px-6 py-3 rounded-lg font-medium hover:bg-primary-50 transition-colors"
          >
            Download Resume
          </a>
        </div>
      </div>
    </div>
  );
};

export default Work;
